// Simple Cart Logic let cart = []; function addToCart(product) { cart.push(product); alert(`${product.name} added to the cart!`); } // Example: you can update product card buttons to call this function document.querySelectorAll('.product-card button').forEach((button, index) => { button.addEventListener('click', () => { const product = { name: `Product ${index + 1}`, price: `$${(index + 1) * 100}`, }; addToCart(product); }); });
